import React from "react";

const Player = ({ q, r }) => {
  // Calculate the pixel coordinates based on your layout and grid size
  const layout = {
    spacing: 1.05,
    size: { x: 6, y: 6 },
    origin: { x: 0, y: 0 },
  };

  // Calculate the position based on hexagon grid coordinates
  const pixelPosition = {
    x: (q * 3 / 2) * layout.size.x * layout.spacing,
    y: ((-q / 2) + r) * layout.size.y * layout.spacing,
  };

  const playerStyle = {
    position: 'absolute',
    width: '20px',
    height: '20px',
    backgroundColor: 'red',
    borderRadius: '50%',
    transform: `translate(${pixelPosition.x}px, ${pixelPosition.y}px)`,
  };

  return <div style={playerStyle}></div>;
};

export default Player;
