import React from "react";

const Player = ({ i }) => {
  // Define the size of your hexagonal grid
  const gridSize = 126; // You can adjust this based on your grid size

  // Calculate q and r based on the given i index
  const q = Math.floor(i / gridSize) - 8;
  const r = i % gridSize - 7;

  // Calculate the pixel offsets for hexagon center-to-center spacing
  const hexSize = 34; // Adjust this value based on your hexagon size
  const xOffset = 3/2 * hexSize * q;
  const yOffset = hexSize * (r + q/2);

  const playerStyle = {
    position: 'absolute',
    width: '20px',
    height: '20px',
    backgroundColor: 'red',
    borderRadius: '50%',
    transform: `translate(${xOffset}px, ${yOffset}px)`, // Adjust these values as needed
  };

  return (
    <div style={playerStyle}></div>
  );
};

export default Player;
