import React from "react";
import playerImage from "../images/cowboy.png"
import { gridArray } from "./Grid"

const Player = ({ q, r }) => {
  

  // const [q, r] = gridArray[i];
  console.log(`q: ${q}, r: ${r}`); // Add this line to check the q and r values


  const playerStyle = {
    position: 'absolute',
    width: `50px`,
    height: `50px`,
    backgroundImage: `url(${playerImage})`, // Use the imported image
    backgroundSize: "cover", // or "contain" depending on how you want to size the image
    // backgroundColor: 'red',
    // borderRadius: '50%', // Create a circle
    // transform: 'translate(-50%, -50%)', // Center the dot

  };
  return (
    <div style={playerStyle}></div>
  );
};

export default Player;
