import React from 'react';
import { Hexagon, Text } from 'react-hexgrid'
import Player from './Player' // Import the Player component


let handleClick = (q, r, i, places) => {

    const adjacentTiles = [
        { q: q, r: r - 1 }, // Top
        { q: q + 1, r: r - 1 }, // Top-Right
        { q: q + 1, r: r }, // Bottom-Right
        { q: q, r: r + 1 }, // Bottom
        { q: q - 1, r: r + 1 }, // Bottom-Left
        { q: q - 1, r: r }, // Top-Left
    ];

    const adjacentTileValues = adjacentTiles.map(tile => {
        const foundPlace = places.find(place => place.q === tile.q && place.r === tile.r);
        return foundPlace ? foundPlace.i : null;
    });

    const message = `Clicked Tile (i): ${i}\nAdjacent Tiles (i): ${adjacentTileValues.join(', ')}`;

    // Your click event logic goes here
    // alert(i);
    console.log(message);
    console.log(i);
}


const places = [
    {i: 0},
    {i: 1},
    {i: 2},
    {i: 17},
    {i: 18},
    {i: 19},
    {i: 35},
];

const getRandomPlace = () => {
    const randomIndex = Math.floor(Math.random() * places.length);
    return places[randomIndex];
};


const randomPlace = getRandomPlace();


const Cell = ({ q, r, i}) => {


    // const isPlayerCell = q === 5 && r === -1; // Change these coordinates as needed

    const isPlayerCell = places.some(place => place.i === i);

    return (
        <Hexagon onClick={()=>handleClick(q, r, i,)} key={i} q={q} r={r}>


            {/* {<Text>
                {i} {q} {r}
            </Text>} */}
            {/* {isPlayerCell ? <Player /> : null} */}
            {randomPlace.i === i ? <Player /> : null}
        </Hexagon>
    )
}
export default Cell
