import React from "react";

const Player = ({ i }) => {
  // Define the size of your hexagonal grid
  const gridSize = 126; // You can adjust this based on your grid size

  // Calculate q and r based on the given i index
  const q = Math.floor(i / gridSize) - 125;
  const r = i % gridSize - 7;

  const playerStyle = {
    position: 'absolute',
    width: '20px',
    height: '20px',
    backgroundColor: 'red',
    borderRadius: '50%',
    transform: `translate(${q * 34}px, ${r * 40}px)`, // Adjust these values as needed
  };

  return (
    <div style={playerStyle}></div>
  );
};

export default Player;
