import React from "react";
import playerImage from "../../../shootem-up-showdown/assets/images/cowboy.png"; // Replace with the actual path to your image

const Player = ({q, r}) => {


  const playerStyle = {
    position: 'absolute',
    left: q, 
    top: r, 
    width: `20px`,
    height: `20px`,
    // backgroundColor: 'red',
    // borderRadius: '50%', // Create a circle
    backgroundImage: `url(${playerImage})`, 
    backgroundSize: "cover", 
    transform: 'translate(-50%, -50%)', // Center the dot

  };
  return (
    <div style={playerStyle}></div>
  );
};

export default Player;
