import React from "react";
import playerImage from "../images/cowboy.png" // Replace with the actual path to your image

const Player = ({q, r}) => {
  const cellSize = 30; // Adjust this to your desired cell size
  const x = q * cellSize * Math.sqrt(3);
  const y = r * cellSize * 1.5;

  const playerStyle = {
    position: 'absolute',
    left: `${x}px`,
    top: `${y}px`,
    width: '20px',
    height: '20px',
    backgroundColor: 'red',
    borderRadius: '50%',
    // backgroundImage: `url(${playerImage})`, 
    // backgroundSize: "cover", 
    // transform: 'translate(-50%, -50%)', // Center the dot

  };
  return (
    <div style={playerStyle}></div>
  );
};

export default Player;
