import React from "react";

const Player = ({ i }) => {
  const gridArray = [
    [-8, 1], [-7, 0], [-6, 0], [-5, -1], [-4, -1], [-3, -2], [-2, -2], [-1, -3], [0, -3], [1, -4], [2, -4], [3, -5], [4, -5], [5, -6], [6, -6], [7, -7], [8, -7],
    [-8, 2], [-7, 1], [-6, 1], [-5, 0], [-4, 0], [-3, -1], [-2, -1], [-1, -2], [0, -2], [1, -3], [2, -3], [3, -4], [4, -4], [5, -5], [6, -5], [7, -6], [8, -6],
    [-8, 3], [-7, 2], [-6, 2], [-5, 1], [-4, 1], [-3, 0], [-2, 0], [-1, -1], [0, -1], [1, -2], [2, -2], [3, -3], [4, -3], [5, -4], [6, -4], [7, -5], [8, -5],
    [-8, 4], [-7, 3], [-6, 3], [-5, 2], [-4, 2], [-3, 1], [-2, 1], [-1, 0], [0, 0], [1, -1], [2, -1], [3, -2], [4, -2], [5, -3], [6, -3], [7, -4], [8, -4],
    [-8, 5], [-7, 4], [-6, 4], [-5, 3], [-4, 3], [-3, 2], [-2, 2], [-1, 1], [0, 1], [1, 0], [2, 0], [3, -1], [4, -1], [5, -2], [6, -2], [7, -3], [8, -3],
    [-8, 6], [-7, 5], [-6, 5], [-5, 4], [-4, 4], [-3, 3], [-2, 3], [-1, 2], [0, 2], [1, 1], [2, 1], [3, 0], [4, 0], [5, -1], [6, -1], [7, -2], [8, -2],
    [-8, 7], [-7, 6], [-6, 6], [-5, 5], [-4, 5], [-3, 4], [-2, 4], [-1, 3], [0, 3], [1, 2], [2, 2], [3, 1], [4, 1], [5, 0], [6, 0], [7, -1], [8, -1],
    [-7, 7], [-5, 6], [-3, 5], [-1, 4], [1, 3], [3, 2], [5, 1], [7, 0]
];

const hexSize = 20; // Adjust this value based on your hexagon size
const hexWidth = Math.sqrt(3) * hexSize;
const hexHeight = 2 * hexSize;
const horizontalSpacing = 3/4 * hexWidth; // Horizontal spacing between hexagons
const verticalSpacing = hexHeight; // Vertical spacing between hexagons

const calculateTop = (q, r) => {
  // Calculate the top position based on q and r
  return r * verticalSpacing + hexHeight / 2;
};

const calculateLeft = (q, r) => {
  // Calculate the left position based on q and r
  return q * horizontalSpacing + (r % 2 === 1 ? horizontalSpacing / 2 : 0);
};


  if (i >= 0 && i < gridArray.length) {
    const [q, r] = gridArray[i];
    const playerStyle = {
      position: 'absolute',
      width: '20px',
      height: '20px',
      backgroundColor: 'red',
      borderRadius: '50%',
      transform: 'translate(-50%, -50%)',
      // Use the q and r coordinates to position the player
      top: calculateTop(q, r),
      left: calculateLeft(q, r),
    };

    return (
      <div style={playerStyle}></div>
    );
  } else {
    // Handle the case where i is out of bounds
    return null;
  }
};

export default Player;
