import React from "react";

const Player = ({q, r}) => {



  const playerStyle = {
    position: 'absolute',
    left: q * 20 + 10, 
    top: r * 20 + 10,
    width: `20px`,
    height: `20px`,
    backgroundColor: 'red',
    borderRadius: '50%', // Create a circle
    // transform: 'translate(-50%, -50%)', // Center the dot

  };
  return (
    <div style={playerStyle}></div>
  );
};

export default Player;
