import React from "react";

const Player = ({ q, r }) => { // Use q and r directly as props
  console.log(`q: ${q}, r: ${r}`); // Add this line to check the q and r values
  const playerStyle = {
    position: 'absolute',
    width: '20px',
    height: '20px',
    backgroundColor: 'red',
    borderRadius: '50%',
    transform: `translate(${q * 20}px, ${r * 20}px)`, // Assuming each grid cell is 20px
    // Use the q and r coordinates to position the player
  };

  return (
    <div style={playerStyle}></div>
  );
};

export default Player;
