import React from 'react';
import { Hexagon, Text } from 'react-hexgrid'
import Player from './Player' // Import the Player component


let handleClick = (i) => {
    // Your click event logic goes here
    alert(i);
    console.log(i)
}

// const places = [
//     {q:0, r: -8},
//     {q:1, r: -7},
//     {q:2, r: -6},
//     {q:18, r: -7},
//     {q:17, r: -8},
//     {q:35, r: -7},
//     {q:19, r: -6}
// ]

const places = [
    {i: 0},
    {i: 1},
    {i: 2},
    {i: 17},
    {i: 18},
    {i: 19},
    {i: 35},
];

const getRandomPlace = () => {
    const randomIndex = Math.floor(Math.random() * places.length);
    return places[randomIndex];
};

const Cell = ({ q, r, i}) => {

    const [randomPlace, setRandomPlace] = useState(null);

    useEffect(() => {
        setRandomPlace(getRandomPlace());
    }, []); // This effect runs once when the component mounts

    // const isPlayerCell = q === 5 && r === -1; // Change these coordinates as needed

    const isPlayerCell = places.some(place => place.i === i);

    return (
        <Hexagon onClick={()=>handleClick(i)} key={i} q={q} r={r}>

            {/* {<Text>
                {i} {q} {r}
            </Text>} */}
            {/* {isPlayerCell ? <Player /> : null} */}
            {randomPlace.i === i ? <Player /> : null}
        </Hexagon>
    )
}
export default Cell
